import {useState} from 'react'

const useForm = () => {

    const [value,setValue]=useState({});  

    const handleChange=(e) => {
      //to safe from refresh 
      e.preventDefault();
      setValue({...value,[e.target.name]:e.target.value});

    }

  return [handleChange]
}

export default useForm
