  return (
    <div>
      <form action>
        <input 
        type="text" 
        name="fullname" 
        onChange={(e)=>handleChange(e)} 
         />
        <input 
        type="text" 
        name="username"  
       onChange={(e)=>handleChange(e)}    
        />
        <input 
        type="password"
         name="password"  
         onChange={(e)=>handleChange(e)}    />
         </form>
    </div>
  )
}

export default ContactUs
